// Retrieve the table data from flow context
var tableData = flow.get("CSV") || [];
var msg_notify;
var itemsToUpdate = msg.payload;

// Check if itemsToUpdate is an array
if (Array.isArray(itemsToUpdate)) {

    // Iterate over each item-quantity pair in the incoming payload
    itemsToUpdate.forEach(function (record) {
        var incomingItem = record.item ? record.item.trim() : null; // Ensure item name is not undefined and trimmed
        var incomingQuantity = record.quantity ? parseFloat(record.quantity) : 0; // Ensure it's a number (default 0 if undefined)

        if (incomingItem && incomingQuantity >= 0) { // Proceed only if both item and quantity are valid

            // Iterate over the table data to find the matching item
            tableData.forEach(function (row) {
                var tableItem = row['Resource'] ? row['Resource'].trim() : null; // Use 'Resource' instead of [0]

                if (tableItem == incomingItem) { // Check for a match
                    var remainingQuantity = row['Remaining Quantity'] != undefined ? parseFloat(row['Remaining Quantity']) : null;

                    if (remainingQuantity != null) {
                        //node.warn("Original remaining quantity for " + incomingItem + ": " + remainingQuantity);
                        remainingQuantity -= incomingQuantity; // Subtract the incoming quantity
                        row['Remaining Quantity'] = remainingQuantity; // Update the remaining quantity in the table
                    }
                }
            });
        } else {
            //node.warn("Invalid item or quantity: " + JSON.stringify(record));
            msg_notify = { "payload": "Invalid item or quantity: " + JSON.stringify(record) };
        }
    });

    // Save the updated table back to flow context
    flow.set("CSV", tableData);

    msg.payload = tableData;
    msg_notify = {"payload": "Inventory Updated!"};
} else {
    //node.warn("Incoming payload is not an array");
    msg_notify = { "payload": "Incoming payload is not an array!" };
}

return [msg,msg_notify];

