//CHECK IF FLOOD SEVERITY HAS RISEN FOR SPECIFIC LOCATION
//AND CALL PYTHON SCRIPTS FOR RESOURCE DISTRIBUTION

//get Location Name (only first element when splitted by whitespace) and flood depth from sensor payload
var get_location = msg.payload[0][1].name.split(' ')[0];
//var get_sensor_flood_depth = msg.payload[0][0].flood_depth;

//calculate potential SEVERITY LEVEL based on value
var new_severity_level = 0;
var get_curr_severity_level = 100;

//boolean control for level change
var severity_level_risen = false;
var severity_level_dropped = false;

switch(get_location) {
  case "Falani":
    get_curr_severity_level = global.get("FLOOD_SEVERITY_FALANI").severity;
    var sensors = global.get("SENSORS_FALANI");
    break;
  case "Larissa":
    get_curr_severity_level = global.get("FLOOD_SEVERITY_LARISSA").severity;
    var sensors = global.get("SENSORS_LARISSA");
    break;
  case "Dasochori":
    get_curr_severity_level = global.get("FLOOD_SEVERITY_DASOCHORI").severity;
    var sensors = global.get("SENSORS_DASOCHORI");
    break;
  case "Omorfochori":
    get_curr_severity_level = global.get("FLOOD_SEVERITY_OMORFOCHORI").severity;
    var sensors = global.get("SENSORS_OMORFOCHORI");
    break;
  default:
    //do nothing - something went wrong
}

//we will find the highest flood depth value
//amongst sensors in the Location
//which will determine the actual new_severity_level
// Initialize the variable to store the highest Flood Depth ( = current sensor flood depth value)
var highest_reading_in_location = msg.payload[0][0].flood_depth;

sensors.forEach(function (sensorData) {
    var floodDepth = sensorData["Flood Depth"];

    // Compare and update the highest_reading_in_location if a higher value is found
    if (highest_reading_in_location === null || floodDepth > highest_reading_in_location) {
        highest_reading_in_location = floodDepth;
    }
});


if (highest_reading_in_location >= global.get("THRESHOLD_CATASTROPHIC")) {
    new_severity_level = 4;

} else if (highest_reading_in_location >= global.get("THRESHOLD_SEVERE")) {
    new_severity_level = 3;

} else if (highest_reading_in_location >= global.get("THRESHOLD_MODERATE")) {
    new_severity_level = 2;

} else if (highest_reading_in_location >= global.get("THRESHOLD")) {
    new_severity_level = 1;
}

//check if level has changed
if (new_severity_level > get_curr_severity_level) {
    //level has risen - change control boolean
    severity_level_risen = true;

} else if (new_severity_level < get_curr_severity_level) {
    //level has dropped
    severity_level_dropped = true;
}

//if severity has DROPPED or RISEN - update global var
if (severity_level_dropped || severity_level_risen) {
//if (severity_level_risen) {
    switch(get_location) {
        case "Falani":
            global.set("FLOOD_SEVERITY_FALANI", { "location": "Falani", "severity": new_severity_level });
            break;
        case "Larissa":
            global.set("FLOOD_SEVERITY_LARISSA", { "location": "Larissa", "severity": new_severity_level });
            break;
        case "Dasochori":
            global.set("FLOOD_SEVERITY_DASOCHORI", { "location": "Dasochori", "severity": new_severity_level });
            break;
        case "Omorfochori":
            global.set("FLOOD_SEVERITY_OMORFOCHORI", { "location": "Omorfochori", "severity": new_severity_level });
            break;
        default:
            //do nothing - something went wrong            
    } 
} 

if (severity_level_risen){    
    //if severity has RISEN - make appropriate msg payload else return null

    //make payload to send to python scripts
    var triggered_flood_depth = msg.payload[0][0].flood_depth;
    msg.payload = [{"location": get_location, "severity": new_severity_level, "flood_depth": triggered_flood_depth}];

