        try {

            let outputString = payloadObj.output.trim();

            // Parse the output string as JSON
            let outputJson = JSON.parse(outputString);

            // Check if the 'script' is "create dataset"
            if (outputJson.script === "create dataset") {
                // Combine 'message', 'script', and 'csv_path' fields into one object
                msg.payload = {
                    message: payloadObj.message,
                    script: outputJson.script,
                    csv_path: outputJson.csv_path  
                };
            } 
            // Check if the 'script' is "train model"
            else if (outputJson.script === "train model") {
                // Combine 'message', 'script', and evaluation metrics into one object
                msg.payload = {
                    message: payloadObj.message,
                    script: outputJson.script,
                    val_mean_absolute_error: outputJson.validation_set_metrics.mean_absolute_error,
                    val_mean_squared_error: outputJson.validation_set_metrics.mean_squared_error,
                    val_r_squared: outputJson.validation_set_metrics.r_squared,
                    val_smape: outputJson.validation_set_metrics.smape + "%",
                    test_mean_absolute_error: outputJson.test_set_metrics.mean_absolute_error,
                    test_mean_squared_error: outputJson.test_set_metrics.mean_squared_error,
                    test_r_squared: outputJson.test_set_metrics.r_squared,
                    test_smape: outputJson.test_set_metrics.smape + "%",
                    trained_model_path: outputJson.trained_model_path
                };
            } 
            // Check if the 'script' is "predict"
            else if (outputJson.script === "predict") {
                // Combine 'message', 'script', and 'decisions' into one object
                msg.payload = {
                    message: payloadObj.message,
                    script: outputJson.script,
                    decisions: outputJson.decisions
                };
            } 
            else {
                // If the script is neither recognized, return an error message
                msg.payload = { error: "Script is not recognized." };
            }
        } catch (e) {            
            msg.payload = { error: "Invalid output format" };
        }
    } else {
        msg.payload = { error: "No output provided" };
    }
} catch (e) {
    msg.payload = { error: "Invalid payload format" };
}

