//1	Falani_North [1:Point]	0	-1	0	0	22,40991275	39,73259648

// Retrieve FALANI sensors
var sensors = global.get("SENSORS_FALANI");

//name 
var get_name = "Falani North [1:Point]";

//location
var get_lon = 22.40991275;
var get_lat = 39.73259648;

//use to read previous values
var get_prev_reading = null;
var get_prev_humidity = null;
var get_prev_temperature = null;

//FLOOD DEPTH
var get_random_water_level = 0;

// Loop through the array to find the sensor and get its values
sensors.forEach(function(sensorData) {
    if (sensorData.sensor === get_name) {
        get_prev_reading = sensorData["Flood Depth"];
        get_prev_humidity = sensorData["Humidity"];
        get_prev_temperature = sensorData["Temperature"];
    }
});

//if previous reading = -1, then it is the initialization phase
if (get_prev_reading == -1){
    // Random initial water level between 0 and 250 mm
    get_prev_reading = Math.floor(Math.random() * 251);
}

if (flow.get("IS_FLOODING") == 1) {
    //Flooding event ongoing - values rise
    // Increase by 10 to 59 mm
    get_random_water_level = get_prev_reading + Math.floor(Math.random() * 50) + 10;

    if (get_random_water_level > 3000) {
        // Cap at 3000 mm
        get_random_water_level = 3000;
    }
} else {
    //Flooding event stopped - values will fall
    // Decrease by 0 to 19 mm
    get_random_water_level = get_prev_reading - Math.floor(Math.random() * 20);

    if (get_random_water_level < 0) {
        // Ensure it doesn't go below 0
        get_random_water_level = 0;
    }
}


//HUMIDITY
//if previous reading = -1, then it is the initialization phase
if (get_prev_humidity == -1){
    // Random initial humidity between 40% and 60%
    get_prev_humidity = Math.floor(Math.random() * 21) + 40;
}

var get_humidity = 0;
if (flow.get("IS_FLOODING") == 1){
    // Gradually increase humidity during flooding
    // Increase by 1 to 3%
    get_humidity = get_prev_humidity + Math.floor(Math.random() * 3) + 1;
    if (get_humidity > 100) {
        // Cap at 100%
        get_humidity = 100;
    }
} else {
    // Gradually decrease humidity when not flooding
    // Decrease by 1 to 3%
    get_humidity = get_prev_humidity - (Math.floor(Math.random() * 3) + 1);
    if (get_humidity < 40) {
        // Ensure it doesn't go below 40%
        get_humidity = 40;
    }
}


//TEMPERATURE
//if previous reading = 999, then it is the initialization phase
if (get_prev_temperature == 999){
    // Random initial temperature between 28C and 30C
    get_prev_temperature = Math.floor(Math.random() * 2) + 28;
}

// Change by -1, 0, or +1 C
var get_temperature = get_prev_temperature + Math.floor(Math.random() * 3) - 1;
if (get_temperature < 25) { get_temperature = 25; }
if (get_temperature > 32) { get_temperature = 32; }


//STORE VALUES TO FLOW VARIABLE
sensors.forEach(function(sensorData) {
    if (sensorData.sensor === get_name) {
        sensorData["Flood Depth"] = get_random_water_level;
        sensorData["Humidity"] = get_humidity;
        sensorData["Temperature"] = get_temperature;
    }
});

// Store the updated sensors array back to the global variable SENSORS_FALANI
global.set("SENSORS_FALANI", sensors);

//FINAL PAYLOAD
msg.payload = [
    [{
        flood_depth: get_random_water_level,
        humidity: get_humidity,
        temperature: get_temperature
    },
    {
        name: get_name,
        lon: get_lon,
        lat: get_lat        
    }]
];
return msg;

