    //reset force AllERT if it was enabled
    if (flow.get("FORCE_AllERT") == 1){
        flow.set("FORCE_AllERT", 0);
    }

    msg.payload[0][1].status = 1;

    if (global.get("RESPONDERS_NOTIFIED") == 0) {

        var notification_msg = {payload: "Urgent! Immediate Action Required for ongoing Flood Response Coordination. Login to the AllERT DSS for information regarding the necessary actions."};
        global.set("RESPONDERS_NOTIFIED", 1);
        return [msg, notification_msg, msg];

    } else {

        return [msg, null, msg];
    }

} else {
    msg.payload[0][1].status = 0;  
    return [msg, null, null];
}

