                threshold_status += " - Moderate Flood.."
                break;
            case 3:
                threshold_status += " - SEVERE Flood !!!"
                break;
            case 4:
                threshold_status += " - CATASTROPHIC FLOOD !!! :("
                break;
            default:
                //do nothing - something went wrong
        }
    }

}
msg.payload = threshold_status;
//msg.enable = enable_status;

return msg;

let data = msg.payload;

let groupedData_Falani = {};
let groupedData_Larissa = {};
let groupedData_Dasochori = {};
let groupedData_Omorfochori = {};

function roundToNearestMinute(timestamp) {
    const minuteInMs = 60 * 1000;
    return Math.round(timestamp / minuteInMs) * minuteInMs;  // Round to the nearest minute
}

function groupByTime(groupedData, time, value) {
    if (!groupedData[time]) {
        groupedData[time] = { sum: 0, count: 0 };
    }
    groupedData[time].sum += value;
    groupedData[time].count += 1;
}

// Loop through InfluxDB results
data.forEach(item => {
    let time = roundToNearestMinute(new Date(item._time).getTime());  // Round time to nearest minute
    let value = item._value;  // Flood depth value
    let name = item.name;  // Tag value for the name

    if (name.startsWith("Falani")) {
        groupByTime(groupedData_Falani, time, value);
    } else if (name.startsWith("Larissa")) {
        groupByTime(groupedData_Larissa, time, value);
    } else if (name.startsWith("Dasochori")) {
        groupByTime(groupedData_Dasochori, time, value);
    } else if (name.startsWith("Omorfochori")) {
        groupByTime(groupedData_Omorfochori, time, value);
    }
});

function getLastSunday(year, month) {
    let lastDay = new Date(year, month + 1, 0);  // Get the last day of the month
    let dayOfWeek = lastDay.getDay();  // Get the day of the week (0 = Sunday, 6 = Saturday)
    let lastSunday = lastDay.getDate() - dayOfWeek;  // Calculate the last Sunday
    return new Date(year, month, lastSunday);
}

function isDaylightSavingInGreece(date) {
    let year = date.getFullYear();

    // Daylight saving starts on the last Sunday of March and ends on the last Sunday of October
    let dstStart = getLastSunday(year, 2);  // March is month 2 (0-based)
    let dstEnd = getLastSunday(year, 9);  // October is month 9 (0-based)

    // Check if the current date is within the DST range
    return date >= dstStart && date < dstEnd;
}

function formatTimestampToLocal(timestamp) {
    let date = new Date(timestamp);

    // Apply timezone offset for Greece (UTC+2 or UTC+3 during daylight saving time)
    let timezoneOffset = isDaylightSavingInGreece(date) ? 3 : 2;  // UTC+3 for daylight saving, UTC+2 otherwise
    date.setHours(date.getUTCHours() + timezoneOffset);

    let day = date.getDate().toString().padStart(2, '0');
    let month = (date.getMonth() + 1).toString().padStart(2, '0');  // Months are zero-based
    let hours = date.getHours().toString().padStart(2, '0');
    let minutes = date.getMinutes().toString().padStart(2, '0');

    return `${day}/${month} ${hours}:${minutes}`;
}

function calculateAverages(groupedData) {
    let chartData = [];
    for (let time in groupedData) {
        let avgValue = groupedData[time].sum / groupedData[time].count;
        // Format the time before adding to chart data, convert to local timezone
        chartData.push({ "x": formatTimestampToLocal(parseInt(time)), "y": avgValue });
    }
    return chartData;
}

// Calculate the average values for each location
let chartData_Falani = calculateAverages(groupedData_Falani);
let chartData_Larissa = calculateAverages(groupedData_Larissa);
let chartData_Dasochori = calculateAverages(groupedData_Dasochori);
let chartData_Omorfochori = calculateAverages(groupedData_Omorfochori);

// Assign the chart data to the corresponding messages
var msg_Falani = (chartData_Falani.length === 0) ? null : { "payload": chartData_Falani };
var msg_Larissa = (chartData_Larissa.length === 0) ? null : { "payload": chartData_Larissa };
var msg_Dasochori = (chartData_Dasochori.length === 0) ? null : { "payload": chartData_Dasochori };
var msg_Omorfochori = (chartData_Omorfochori.length === 0) ? null : { "payload": chartData_Omorfochori };

return [msg_Falani, msg_Larissa, msg_Dasochori, msg_Omorfochori];

query:
//used mapping for Falani and Larissa
//since some points were inserted 
//ex. as Falani_North and some as Falani North
//in order to include both in the results

from(bucket: "DemieRed")
  |> range(start: -30d)  // Retrieves data from the past 8 days.
  |> filter(fn: (r) => r._measurement == "AllERT_Sensors")
  |> filter(fn: (r) => r._field == "flood_depth")  // Filter by the flood_depth field.
  |> map(fn: (r) => ({
      _time: r._time,
      _value: r._value,
      name: if r.name =~ /^Falani/ then "Falani"
            else if r.name =~ /^Larissa/ then "Larissa"
            else if r.name =~ /^Dasochori/ then "Dasochori"
            else if r.name =~ /^Omorfochori/ then "Omorfochori"
            else r.name  // Leave unchanged if none of the patterns match
  }))
  |> aggregateWindow(every: 1h, fn: mean, createEmpty: false)  // Aggregate the data to hourly means.
  |> keep(columns: ["_time", "_value", "name"])  // Keep only the time, flood_depth (stored in _value), and the tag key.
  |> sort(columns: ["_time"], desc: false)  // Sort by _time in ascending order.
  |> limit(n: 1000)  // Limit the number of results returned (optional, adjust as needed).
  |> yield(name: "result")

